mapper.select:
SELECT ord_code, usr_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate, ord_admin_memo FROM ( SELECT /*+ INDEX_DESC(order_table PK_ORD_CODE) */ ROWNUM AS RN, ord_code, usr_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate, ord_admin_memo FROM order_table WHERE
ROWNUM <= (#{cri.pageNum} * #{cri.amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{cri.pageNum} - 1 ) * #{cri.amount}

mapper.delete:
DELETE FROM
order_detail_table
WHERE
ord_code = #{ord_code}
AND
book_bno = #{book_bno}

mapper.update:
UPDATE
payment_table
SET
pay_price = (
SELECT COALESCE(SUM(dt_amount * dt_price), 0) FROM order_detail_table
WHERE ord_code = #{ord_code}
)
WHERE
ord_code = #{ord_code}
