mapper.select:
SELECT ord_code, usr_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate FROM ( SELECT /*+ INDEX_DESC(order_table PK_ORD_CODE) */ ROWNUM AS RN, ord_code, usr_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate FROM order_table WHERE
ROWNUM <= (#{pageNum} * #{amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{pageNum} - 1 ) * #{amount}
