mapper.select:
SELECT book_bno, c_code, book_name, book_author, book_publishing_date, book_deposit, book_deposit_discount, book_publisher, book_content, book_up_folder, book_img, book_amount, book_loan, book_date, book_updatedate FROM ( SELECT /*+ INDEX_DESC(book_table PK_BOOK_BNO) */ ROWNUM AS RN, book_bno, c_code, book_name, book_author, book_publishing_date, book_deposit, book_deposit_discount, book_publisher, book_content, book_up_folder, book_img, book_amount, book_loan, book_date, book_updatedate FROM book_table WHERE c_code = #{c_code} AND
ROWNUM <= (#{cri.pageNum} * #{cri.amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{cri.pageNum} - 1 ) * #{cri.amount}

mapper.update:

UPDATE
	book_table
SET
	book_amount = book_amount - #{book_amount}
WHERE
	book_bno = #{book_bno}
